CREATE VIEW v AS
SELECT person_details.pid,person_details.lid,sal,fname,lname,phone,email,mobile,dob,anniversary,source_details.sid,src_name,src_desc,category_details.catid,cat_name,company_details.cmpid,cmp_name,designation,fax,address_details.aid,c_addr1,c_addr2,c_addr3,city,pincode,country,art_event,book_event,alcohol_pairing,fashion_event,vip_event,sports_event,food_promo,fund_raiser
FROM person_details,address_details,company_details,source_details,person_source,category_details,person_category,person_event,book_event_details,art_event_details,food_promo_details,alcohol_pairing_details,fund_raiser_details,fashion_event_details,sports_event_details,vip_event_details
WHERE company_details.cmpid = address_details.cmpid and person_details.pid = company_details.pid and person_source.sid = source_details.sid and person_details.pid = person_source.pid and person_details.pid=person_category.pid and person_category.catid = category_details.catid and person_details.pid = person_event.pid and book_event_details.bookid = person_event.bookid  and art_event_details.artid = person_event.artid and food_promo_details.foodid = person_event.foodid and alcohol_pairing_details.alcid = person_event.alcid and fund_raiser_details.fundid = person_event.fundid and fashion_event_details.fashionid = person_event.fashionid and sports_event_details.sportsid = person_event.sportsid and vip_event_details.vipid = person_event.vipid